insert into "books" ("img_url", "publisher", "summary", "title", "year") values ('http://books.google.com/books/content?id=W260hhZVhI0C&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Springer Science & Business Media', 'Here are the proceedings of the First International Workshop on Haptic and Audio Interaction Design, HAID 2006. The book presents 15 revised full papers, in a variety of disciplines ranging from psychology to art, showcasing how haptics and sound can improve user interaction with computers, helping people with various kinds of disabilities and visual impairment. Also addresses psychophysics, art and leisure, and mobile applications that improve selections in virtual environments.', 'Haptic and Audio Interaction Design', '2006-08-10');
